class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            isLoaded: false,
        }
    }

    componentDidMount() {
        fetch('./data/data.json')
            .then(res => res.json())
            .then(data => {
                this.state({
                   data,
                   isLoaded: true,
               });
            })
    }

    render() {
        const { isLoaded } = this.state;
        if (isLoaded) {
            return (
                <div className="App">
                    <h1>Hello React</h1>
                    {/*{data.content.body.map(block => Components(block))}*/}
                </div>
            );
        }
        return null;
    }
}

const rootElement = document.getElementById("root");
