class App extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            data: {},
            isLoading: false
        }
    }

    getJson(){
        fetch(`./data/data.json`)
            .then( (response) => {
                return response.json()
            })
            .then( (json) => {
                this.setState({
                      data: json.content.body,
                      isLoading: false
                  })
            });
    }

    componentDidMount() {
        this.setState({ isLoading: true });
        this.getJson();
    }

    render() {
        const { data, isLoading } = this.state;

        if (isLoading) {
            return <p>Loading ...</p>;
        }

        console.log(this.state.data);

        return (
            <div>
                <h1>React Json components</h1>
                {this.state.data.map(block => Components(block))}
            </div>
        );
    }
}

ReactDOM.render(
    <App/>,
